import React, { Component } from 'react';
import autosize from "autosize";

class textarea extends Component {
    componentDidMount() {
        this.textarea.focus();
        autosize(this.textarea);
        this.textarea1.focus();
        autosize(this.textarea1);
      }
    render() {

        return (
            <>
                 <div className="col-md-3">

                       <div className="form-group placeholder-label ">
                             <textarea

                                ref={c => (this.textarea = c)}
                                rows={1}
                                defaultValue=""
                                required
                                />
                             <label>Label</label>
                       </div>

                </div>
                <div className="col-md-3">

                       <div className="form-group placeholder-label ">
                             <textarea

                                ref={c => (this.textarea1 = c)}
                                rows={1}
                                defaultValue=" Alice was not a bit hurt, and she"
                                required
                                ></textarea>
                             <label>Label</label>
                       </div>

                </div>
                <div className="col-md-3">

                       <div className="form-group placeholder-label ">
                             <textarea

                                ref={c => (this.textarea = c)}
                                rows={1}
                                defaultValue="Alice was not a bit hurt, and she
                                jumped up on to her feet in a
                                moment"
                                required
                                ></textarea>
                             <label>Label</label>
                       </div>

                </div>
                <div className="w-100 pull-left mb-30"></div>

                <div className="col-md-3">

                  <div className="form-group lg-textarea placeholder-label ">
                        <textarea

                           ref={c => (this.textarea = c)}
                           rows={1}
                           defaultValue=""
                           required
                           />
                        <label>Label</label>
                  </div>

           </div>
           <div className="col-md-3">

                  <div className="form-group lg-textarea placeholder-label ">
                        <textarea

                           ref={c => (this.textarea = c)}
                           rows={1}
                           defaultValue=" Alice was not a bit hurt, and she"
                           required
                           ></textarea>
                        <label>Label</label>
                  </div>

           </div>
           <div className="col-md-3">

                  <div className="form-group lg-textarea placeholder-label ">
                        <textarea

                           ref={c => (this.textarea = c)}
                           rows={1}
                           defaultValue="Alice was not a bit hurt, and she
                           jumped up on to her feet in a
                           moment"
                           required
                           ></textarea>
                        <label>Label</label>
                  </div>

           </div>


            </>
        );
    }
}

export default textarea;
