import React, { Component } from 'react';

class InputSlider extends Component {
  constructor(props) {
    super(props)
    this.state = {

    value: this.state.defaultValue
    }
  }
  // state = {
  //   value: this.state.defaultValue
  // }
  handleOnChange = (e) => {
    const { defaultValue } = e.target.value;
    this.setState({ defaultValue });
  }
  render() {
    const { id, value, label, disabled, size, min, max, ...inputProps } = this.props;
    return (
      <>
          <div className="range-slider">
            <input type="range" min={min} max={max}  defaultValue={value} onChange={this.handleOnChange}/>
            <span>{value}</span>
          </div>
      </>
    );
  }
}

export default InputSlider;
