import React, { Component } from 'react';
const tableData = [];
const tableHead = ["Username", "E-mail", "Status", "Revenue", "Comment"];
class tableStriped extends Component {
    constructor(props) {
      super(props) //since we are extending class Table so we have to use super in order to override Component class constructor
        this.state = { //state is by default an object
          tableData: [
              {
                  id: 1,
                  userName: "SvenBrand1985",
                  emailId: "svenbrand1985@email.com",
                  status: "Active",
                  Revenue: "$ 1,402.56",
                  comment: "Add comment..."
              },
              {
                  id: 2,
                  userName: "AlexDavids45",
                  emailId: "alexdavids45@email.com",
                  status: "Disabled",
                  Revenue: "$ 892.46",
                  comment: "Brief text comment"
              },
              {
                  id: 3,
                  userName: "Sandy333",
                  emailId: "sandy333@email.com",
                  status: "Active",
                  Revenue: "$ 873.00",
                  comment: "Add comment..."
              },
              {
                  id: 4,
                  userName: "SveDevonHillnBrand1985",
                  emailId: "devonhill@email.com",
                  status: "Active",
                  Revenue: "$ 401.24",
                  comment: "Add comment..."
              },
              {
                  id: 5,
                  userName: "EdwardHorton",
                  emailId: "edwardhorton@email.com",
                  status: "Active",
                  Revenue: "$ 302.56",
                  comment: "Add comment..."
              },
              {
                  id: 6,
                  userName: "Megan145",
                  emailId: "megan145@email.com",
                  status: "Disabled",
                  Revenue: "$ 200.01",
                  comment: "Add comment..."
              },
              {
                  id: 7,
                  userName: "Charles654432",
                  emailId: "charles654432@email.com",
                  status: "Active",
                  Revenue: "$ 104.80",
                  comment: "Add comment..."
              },
              {
                  id: 8,
                  userName: "AnnSmith",
                  emailId: "annsmith@email.com",
                  status: "Active",
                  Revenue: "$ 98.15",
                  comment: "Add comment..."
              },
          ],
          tableHead,
        };
    };

    lastSelectedIndex = 0;
    state = {
        data: tableData.map(dataItem => Object.assign({ selected: false }, dataItem))
    }

    selectionChange = (event) => {
        const data = this.state.data.map(item=>{
            if(item.id === event.dataItem.id){
                item.selected = !event.dataItem.selected;
            }
            return item;
        });
        this.setState({ data });
    }
    rowClick = event => {
        let last = this.lastSelectedIndex;
        const data = [...this.state.data];
        const current = data.findIndex(dataItem => dataItem === event.dataItem);

        if (!event.nativeEvent.shiftKey) {
            this.lastSelectedIndex = last = current;
        }

        if (!event.nativeEvent.ctrlKey) {
            data.forEach(item => (item.selected = false));
        }
        const select = !event.dataItem.selected;
        for (let i = Math.min(last, current); i <= Math.max(last, current); i++) {
            data[i].selected = select;
        }
        this.setState({ data });
    };

    headerSelectionChange = (event) => {
        const checked = event.syntheticEvent.target.checked;
        const data = this.state.data.map(item=>{
            item.selected = checked;
            return item;
        });
        this.setState({ data });
    }
    render() {
        return (
            <div className="table-responsive bordered-table mb-60">
                <div className="table-header">
                    <div className="h6">Table Header</div>
                    <div className="button">
                        <div className="sort-icon">

                        </div>
                        <div className="dots-icon">

                        </div>
                    </div>
                </div>
                <table className="table-striped" data={this.state.data}
                    style={{ height: '400px' }}
                    selectedField="selected"
                    onSelectionChange={this.selectionChange}
                    onHeaderSelectionChange={this.headerSelectionChange}
                    onRowClick={this.rowClick}>
                    <thead>
                        <tr>
                            <th style={{width:"20px"}}>
                                <div className="custom-checkbox">
                                    <input type="checkbox" id="all" onChange={this.handleInputChange}/>
                                    <label for="all"></label>      
                                </div>
                            </th>
                            {tableHead.map(data => (
                                <th key={data} className="text-gray text-left">{data}</th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>    
                        {this.state.tableData.map((data, i) => {
                            return  <tr key={data.id}>
                                        <td key={data}>
                                            <div className="custom-checkbox">
                                                <input type="checkbox" id={data.id} id="selected"
                                                width="50px"
                                                headerSelectionValue={
                                                    this.state.data.findIndex(dataItem => dataItem.selected === false) === -1
                                                }/>
                                                <label for={data.id}></label>      
                                            </div>
                                        </td>
                                        <td>{data.userName}</td>
                                        <td>{data.emailId}</td>
                                        <td>{data.status}</td>
                                        <td>{data.Revenue}</td>
                                        <td>{data.comment}</td>
                                    </tr>
                        })}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default tableStriped;
