import React, { Component} from 'react';
import  InputRange  from './InputSlider';

class noValueSlider extends Component {
    state = {
        range: 0
    }
    handleOnChange = (e) => this.setState({range: e.target.range})
    render() {
        return (
            <form className="form">                
                <InputRange min={0} max={100} value={0}  onChange={this.handleOnChange}/>
            </form>
        );
    }
}


export default noValueSlider;
