import React, { Component } from 'react';

class InputSlider extends Component {
  constructor(props) {
        super(props);
        this.state = {
        rangeVal: 0
        }
        this.updateRange = this.updateRange.bind(this);
    }

    updateRange(val) {
        this.setState({
        rangeVal: val
        })
    } 
  handleOnChange = (e) => this.setState({value: e.target.value})
  render() {
    const { rangeVal } = this.state;
    const { id, value, label, disabled, size, min, max, ...inputProps } = this.props;
    return (
      <div>
          <div className="range-slider">
            <input type="range" min={min} max={max} value={value}  rangeVal={rangeVal}/>
            <span>{value}</span>
          </div>
      </div>
    );
  }
}

export default InputSlider;
