import React, { Component } from 'react';
import PropTypes from 'prop-types';

class Checkboxdone extends Component {
  static propTypes = {
    hasError: PropTypes.bool,
    id: PropTypes.string.isRequired,
    indeterminate: PropTypes.bool,
    label: PropTypes.string.isRequired,
    type: PropTypes.oneOf(['default', 'switch']),
  };
  static defaultProps = {
    hasError: false,
    indeterminate: undefined,
    type: 'default',
  };

  componentDidMount() {
    // Apply the indeterminate attribute of the checkbox input
    this.selector.indeterminate = this.props.indeterminate;
  }

  componentDidUpdate(prevProps) {
    if (prevProps.indeterminate !== this.props.indeterminate) {
      this.selector.indeterminate = this.props.indeterminate;
    }
  }

    render() {

        const { id, label, type, color, indeterminate, hasError,red,orange,yellow,green, ...inputProps } = this.props;
    const checkboxClassname = `
    m-checkbox 
      ${type === 'switch' && 'm-checkbox--switch'}
      ${hasError && 'm-checkbox--has-error'}
    `;

    const inputClassname = `
      m-checkbox__input

      ${type === 'switch' && 'm-checkbox--switch__input'}
      ${hasError && 'm-checkbox--has-error__input'}
      ${red && 'c-red'}
      ${orange && 'c-orange'}
      ${yellow && 'c-yellow'}
      ${La_Rioja && 'c-La-Rioja'}
      ${green && 'c-green'}
      ${yellow && 'c-yellow'}



    // --green:#077f23;
    // --Pelorous: #2ba3b2;
    // --Daisy-Bush: #502aa0;
    // --Violet: #8229a0;
    // --Mulberry: #c54b8c;
    // --Korma: #804e2c;
    // --Buttercup: #da9429;
    // --White-Smoke: #f2f2f2;
    // --white: #ffffff;
    `;

    const labelClassname = `
      m-checkbox__label
      ${type === 'switch' && 'm-checkbox--switch__label'}
    `;
        return (
            <>
               <div className="col-md-3">
                 <div className={checkboxClassname}>
                    <input
                      type="checkbox"
                      className={inputClassname}
                      ref={el => (this.selector = el)}
                      id={id}
                      {...inputProps}
                    />
                      <label className={labelClassname} htmlFor={id}>{label}</label>
                 </div>
               </div>
            </>
        ); 
    }
}

export default Checkboxdone;
