import React, { Component } from 'react';

class nolableChips extends Component {
    constructor(props)
    {
        super(props)
        this.state = {
            colorChips: [
                {
                    id: 1,
                    color: 'red',
                    checked: true
                },
                {
                    id: 2,
                    color: 'yellow',
                    checked: true
                },
                {
                    id: 3,
                    color: 'green',
                    checked: true
                },
                {
                    id: 4,
                    color: 'pelorous',
                    checked: true
                },
                {
                    id: 5,
                    color: 'smoke',
                    checked: true
                }
            ]
        }
    }
    render() {
        return (
            <div className="input-chips-wrapper no-label-chips">
                <div className="row">
                    <div className="col-md-4">
                        <div className="flex-div">
                            {this.state.colorChips.map((chipsList, i) => 
                                <div className="form-group input-chips squared" key={i}> 
                                    <input name="selection" type="radio" checked={chipsList.checked}  id={i.id} className={chipsList.color}/>
                                    <label className={chipsList.color} htmlFor={i.id}></label>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default nolableChips;
