import React, { Component } from 'react';

const tableHead = ["Username", "E-mail", "Status", "Revenue", "Comment"];
class tableStriped extends Component {
    constructor(props) {
      super(props) //since we are extending class Table so we have to use super in order to override Component class constructor
        this.state = { //state is by default an object
          tableData: [
              {
                  id: 1,
                  userName: "SvenBrand1985",
                  emailId: "svenbrand1985@email.com",
                  status: "Active",
                  Revenue: "$ 1,402.56",
                  comment: "Add comment..."
              },
              {
                  id: 2,
                  userName: "AlexDavids45",
                  emailId: "alexdavids45@email.com",
                  status: "Disabled",
                  Revenue: "$ 892.46",
                  comment: "Brief text comment"
              },
              {
                  id: 3,
                  userName: "Sandy333",
                  emailId: "sandy333@email.com",
                  status: "Active",
                  Revenue: "$ 873.00",
                  comment: "Add comment..."
              },
              {
                  id: 4,
                  userName: "SveDevonHillnBrand1985",
                  emailId: "devonhill@email.com",
                  status: "Active",
                  Revenue: "$ 401.24",
                  comment: "Add comment..."
              },
              {
                  id: 5,
                  userName: "EdwardHorton",
                  emailId: "edwardhorton@email.com",
                  status: "Active",
                  Revenue: "$ 302.56",
                  comment: "Add comment..."
              },
              {
                  id: 6,
                  userName: "Megan145",
                  emailId: "megan145@email.com",
                  status: "Disabled",
                  Revenue: "$ 200.01",
                  comment: "Add comment..."
              },
              {
                  id: 7,
                  userName: "Charles654432",
                  emailId: "charles654432@email.com",
                  status: "Active",
                  Revenue: "$ 104.80",
                  comment: "Add comment..."
              },
              {
                  id: 8,
                  userName: "AnnSmith",
                  emailId: "annsmith@email.com",
                  status: "Active",
                  Revenue: "$ 98.15",
                  comment: "Add comment..."
              }
          ],
          tableHead,
        };
    };


    render() {
        return (
            <div className="table-responsive bordered-table mb-60">
                <div className="table-header">
                    <div className="h6">Table Header</div>
                    <div className="button">
                        <div className="sort-icon">

                        </div>
                        <div className="dots-icon">

                        </div>
                    </div>
                </div>
                <table className="table-striped">
                    <thead>
                        <tr>
                            <th style={{width:"20px"}}>
                                <div className="custom-checkbox">
                                    <input type="checkbox" checked={this.state.ItemsChecked}
                                    onClick={this.selectItem}/>
                                    <label for="all"></label>      
                                </div>
                            </th>
                            {tableHead.map(data => (
                                <th key={data} className="text-gray text-left">{data}</th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>    
                        {tableData.map((data) => {
                            return <tr key={data.id}>
                                        <td key={data.i}>
                                            <div className="custom-checkbox">
                                                <input type="checkbox" key={data.id}
                                                click={this.openModal}
                                                selectedItems={this.selectedItems.bind(this)}
                                                ItemsChecked={ItemsChecked}
                                                checkedListAll={checkedListAll}
                                                handleCheckboxClick={this.handleCheckboxClick}/>
                                                <label for="all"></label>      
                                            </div>
                                        </td>
                                        <td>{data.userName}</td>
                                        <td>{data.emailId}</td>
                                        <td>{data.status}</td>
                                        <td>{data.Revenue}</td>
                                        <td>{data.comment}</td>
                                    </tr>
                        })}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default tableStriped;
