import React, { Component } from 'react';

class InputSlider extends Component {
  state = {
        range: 0
    }
    handleOnChange = (e) => this.setState({range: e.target.range})
  render() {
    const { id,value,label,disabled,size,min,max, ...inputProps } = this.props;
    return (
      <div>
          <div className="range-slider">
            <input type="range" min={min} max={max} value={value}  onChange={this.handleOnChange}/>
            {/* <span>{value.value}</span> */}
          </div>
      </div>
    );
  }
}

export default InputSlider;
