import React, { Component } from 'react';

class InputSlider extends Component {
  constructor() {
    super()
    this.state = {
      range: [
        { id: 1, value: 0 },
        { id: 2, value: 50 },
        { id: 3, value: 100},
      ],
    }
  }
    handleOnChange = (e) => this.setState({range: e.target.range})

  render() {
    return (
      <div>
        {this.state.range.map(data =>
          <div className="range-slider">
            <input type="range" min={0} max={100} value={data.value} onChange={this.handleOnChange}/>
            {/* <span>{value.value}</span> */}
          </div>
        )}
      </div>
    );
  }
}

export default InputSlider;
