import React, { Component} from 'react';
import  InputRange  from './InputSlider';

class noValueSlider extends Component {
    constructor(props) {
        super(props);
        this.state = {
        rangeVal: 0
        }
        this.updateRange = this.updateRange.bind(this);
    }

    updateRange(val) {
        this.setState({
        rangeVal: val
        })
    } 
    render() {
        const { rangeVal } = this.state;
        return (
            <form className="form">                
                <InputRange min={0} max={100} value={0} rangeVal={rangeVal}  onChange={this.handleOnChange}/>
            </form>
        );
    }
}


export default noValueSlider;
