import React, { Component } from 'react';
function CheckBox({id,name, value, tick, onCheck}) {
  return (
      <><input type="checkbox" id={id} name={name}
                type="checkbox"
                value={value}
                checked={tick || false}
                onChange={onCheck}/>
      <label for={id}></label>   </>
        // <label>
        //     <input
        //         name={name}
        //         type="checkbox"
        //         value={value}
        //         checked={tick || false}
        //         onChange={onCheck}
        //     />
        //     {value}
        // </label>
    );
}
function CheckBoxList ({options, isSelectedAll, onCheck}) {
    const checkBoxOptions = (
        <div className="checkbox-list">
            {options.map((option, index) => {
                return (
                    <CheckBox key={index} name={option.name} value={option.value} tick={option.checked} onCheck={(e) => onCheck(option.value, e.target.checked)} />
                );
            })}
        </div>
    );
    return (
        <div className="checkbox-list">
            <CheckBox name="select-all" value="ALL" tick={isSelectedAll} onCheck={(e) => onCheck('all', e.target.checked)} />
            {checkBoxOptions}
        </div>
    );
}
const tableHead = ["Username", "E-mail", "Status", "Revenue", "Comment"];
class tableStriped extends Component {
    constructor(props) {
      super(props) //since we are extending class Table so we have to use super in order to override Component class constructor
      this.state = { //state is by default an object
          isSelectedAll: false,
          tableData: [
              {
                  id: 1,
                  userName: "SvenBrand1985",
                  emailId: "svenbrand1985@email.com",
                  status: "Active",
                  Revenue: "$ 1,402.56",
                  comment: "Add comment..."
              },
              {
                  id: 2,
                  userName: "AlexDavids45",
                  emailId: "alexdavids45@email.com",
                  status: "Disabled",
                  Revenue: "$ 892.46",
                  comment: "Brief text comment"
              },
              {
                  id: 3,
                  userName: "Sandy333",
                  emailId: "sandy333@email.com",
                  status: "Active",
                  Revenue: "$ 873.00",
                  comment: "Add comment..."
              },
              {
                  id: 4,
                  userName: "SveDevonHillnBrand1985",
                  emailId: "devonhill@email.com",
                  status: "Active",
                  Revenue: "$ 401.24",
                  comment: "Add comment..."
              },
              {
                  id: 5,
                  userName: "EdwardHorton",
                  emailId: "edwardhorton@email.com",
                  status: "Active",
                  Revenue: "$ 302.56",
                  comment: "Add comment..."
              },
              {
                  id: 6,
                  userName: "Megan145",
                  emailId: "megan145@email.com",
                  status: "Disabled",
                  Revenue: "$ 200.01",
                  comment: "Add comment..."
              },
              {
                  id: 7,
                  userName: "Charles654432",
                  emailId: "charles654432@email.com",
                  status: "Active",
                  Revenue: "$ 104.80",
                  comment: "Add comment..."
              },
              {
                  id: 8,
                  userName: "AnnSmith",
                  emailId: "annsmith@email.com",
                  status: "Active",
                  Revenue: "$ 98.15",
                  comment: "Add comment..."
              },
          ],
          tableHead,
        };
    };


    render() {
        return (
            <div className="table-responsive bordered-table mb-60">
                <div className="table-header">
                    <div className="h6">Table Header</div>
                    <div className="button">
                        <div className="sort-icon">

                        </div>
                        <div className="dots-icon">

                        </div>
                    </div>
                </div>
                <table className="table-striped">
                    <thead>
                        <tr>
                            <th style={{width:"20px"}}>
                    <div className="custom-checkbox">
                      <CheckBox />
                                    {/* <input type="checkbox" id="all" onChange={this.handleInputChange}/>
                                    <label for="all"></label>       */}
                                </div>
                            </th>
                            {tableHead.map(data => (
                                <th key={data} className="text-gray text-left">{data}</th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>    
                        {this.state.tableData.map((data, i) => {
                            return  <tr key={data.id}>
                              <td key={data}>
                                <CheckBoxList options={this.state.checkList} isCheckedAll={this.state.isCheckedAll} onCheck={this.onCheckBoxChange.bind(this)} />
                                            {/* <div className="custom-checkbox">
                                                <input type="checkbox" id={data.id} onChange={this.handleInputChange}/>
                                                <label for={data.id}></label>      
                                            </div> */}
                                        </td>
                                        <td>{data.userName}</td>
                                        <td>{data.emailId}</td>
                                        <td>{data.status}</td>
                                        <td>{data.Revenue}</td>
                                        <td>{data.comment}</td>
                                    </tr>
                        })}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default tableStriped;
