import React, { Component } from 'react';

class InputSlider extends Component {
  state = {
    value: 0
  }

  handleOnChange = (e) => {
    const { value } = e.target;
    this.props.handleUpdateColor(this.props.hexColor, value);
    this.setState({ value });
  }
//  constructor(props) {
