import React, { Component } from 'react';

class InputSlider extends Component {
  state = {
    value: ["0","50","100"]
  }

  render() {
    const { id, value, label, disabled, size, min, max, ...inputProps } = this.props;
    return (
      <>
        {this.state.value.map(data =>
          <div className="range-slider">
            <input type="range" min={min} max={max}  defaultValue={data} onChange={this.handleOnChange}/>
            <span>{data}</span>
          </div>
        )}
      </>
    );
  }
}

export default InputSlider;
