import React, { Component } from 'react';

class InputSlider extends Component {
  state = {
    range: [
      {
        value: 0,
        value: 50,
        value: [
          {
            min: 10,
            max: 50
          }
        ]
      }
    ],
    }
    handleOnChange = (e) => this.setState({range: e.target.range})

  render() {
    return (
      <>
        {this.state.range.map(value =>
          <div className="range-slider">
            <input type="range" min={0} max={100} value={this.state.range} onChange={this.handleOnChange}/>
            <span>{this.state.range}</span>
          </div>
        )}
      </>
    );
  }
}

export default InputSlider;
