import React, { Component } from 'react';
class InputSlider extends Component {
  constructor (props) {
    // value: 0
    super(props)
    this.handleOnChange = this.handleOnChange.bind(this);
  }


    handleOnChange = (e) => {
      this.setState({ value: e.target.value });
    }
  static defaultProps = {
        hasError: false,
        hassuccess: false,
        type: 'default',
        size: 'default',
      };
  render() {
    const { id, max, min, value, size, ...inputProps } = this.props;
    return (
      <>
        <div className="range-slider">
          <input type="range" defaultValue={event => this.setState({dueDate: event.target.value})} onChange={this.handleOnChange} />
          <span>{value}</span>
        </div>
      </>
    );
  }
}

export default InputSlider;
