import React, { Component } from 'react';

class InputSlider extends Component {

  handleOnChange = (e) => {
    const { value } = e.target;
    this.setState({ value });
  }
  render() {
    const { id, value, label, disabled, size, min, max, ...inputProps } = this.props;
    return (
      <div>
          <div className="range-slider">
            <input type="range" min={min} max={max}  onChange={this.handleOnChange}/>
                {/* <span >{this.state.value}</span> */}
          </div>
      </div>
    );
  }
}

export default InputSlider;
