class InputSlider extends Component {
    render() {
        return (
            <div>
              <input type="range" name="quantity" min="0" max="100" onInput={this.changeValue} value={this.state.value} />
              <output for="quantity">{this.state.value}</output>
            </div>
        );
    }
}

export default InputSlider;
