import React, { Component } from 'react';
class InputSlider extends Component {
  // state = {
  //   value: 0
  // }

  render() {
    const { id, max, min, value, size, ...inputProps } = this.props;
    handleOnChange = (e) => {
      this.setState({ value: e.target.value });
    }
    return (
      <>
        <div className="range-slider">
          <input type="range" defaultValue={value} onChange={this.handleOnChange} />
          <span>{value}</span>
        </div>
      </>
    );
  }
}

export default InputSlider;
