import React, { Component } from 'react';
class InputSlider extends Component {
  state = {
    value: this.props.value,
    id: this.props.id,
    size: this.props.size,
    min: this.props.min,
    max: this.props.max,
    step: this.props.step,
  }
  handleOnChange = (e) => {
    this.setState({ value: e.target.value });
  }
  handleOnChangeMinMax = (e) => {
    this.setState({ min: e.target.min, max: e.target.max });
  }
  render() {
    const {id,value,size,min,max,step, ...inputProps} = this.props
    return (
      <>
        <div className="range-slider">
          <input type="range" defaultValue={this.state.value} onChange={this.handleOnChange} step={this.state.step} size={this.state.size}/>
          <span>{this.state.value}</span>
        </div>
      </>
    );
  }
}

export default InputSlider;
