import React, { Component } from 'react';

class InputSlider extends Component {

  handleOnChange = (e) => {
    this.setState({ value: e.target.value });
  }
  render() {
    const { id, value, label, disabled, size, min, max, ...inputProps } = this.props;
    return (
      <>
          <div className="range-slider">
            <input type="range" min={min} max={max}  defaultValue={this.state.value} onChange={this.handleOnChange}/>
            <span>{value}</span>
          </div>
      </>
    );
  }
}

export default InputSlider;
