import React, { Component } from 'react';

class InputSlider extends Component {
  constructor() {
    super()
    this.state = {
      range: [
        { value: 0 },
        { value: 50 },
        {value: 100}
      ],
    }
  }
    handleOnChange = (e) => this.setState({range: e.target.range})

  render() {
    return (
      <>
        <div>
        {this.state.range.map(value =>
          <div className="range-slider">
            <input type="range" min={0} max={100} value={value.value} onChange={this.handleOnChange}/>
            {/* <span>{value.value}</span> */}
          </div>
        )}</div>
      </>
    );
  }
}

export default InputSlider;
