import React, { Component } from 'react';
import { Icon } from 'react-icons-kit';
import {x} from 'react-icons-kit/feather/x';
import {mapPin} from 'react-icons-kit/feather/mapPin'

class inputChips extends Component {
    constructor(props)
    {
        super(props)
        this.state = {
            inputChips: [
                {
                    id: 1,
                    Title: 'Enabled',
                    class: 'form-group input-chips default-chips left-icon'
                },
                {
                    id: 2,
                    Title: 'Disabled',
                    class: 'form-group input-chips default-chips disabled left-icon',
                },
                {
                    id: 3,
                    Title: 'Hover',
                    class: 'form-group input-chips default-chips focus left-icon',
                },
            ],
            class: [
                {
                    Default: 'form-group input-chips default-chips left-icon'
                },
                {
                    Outline: 'form-group input-chips outline-chips left-icon'
                },
                {
                    Filled: 'form-group input-chips filled-chips left-icon'
                },
            ]
        }
    }
    render() {
        return (
            <div className="input-chips-wrapper">
                <div className="row">
                    <div className="col-md-4">
                        <div className="default">
                            {this.state.inputChips.map((chipsList, i) => 
                                <div className={chipsList.class} key={i}> 
                                    <i className="inputicon"><Icon icon={mapPin} size={16} /></i>
                                    <input name="selection" type="text" value={chipsList.Title} readOnly id={i.id} />
                                    <i className="cross-icon"><Icon icon={x} size={15}/></i>
                                </div>
                            )}
                            {this.state.inputChips.map((chipsList, i) => 
                                <div className={chipsList.class} key={i}> 
                                    <i className="inputicon"><Icon icon={mapPin} size={16} /></i>
                                    <input name="selection" type="text" value={chipsList.Title} readOnly id={i.id} className="rounded"/>
                                    <i className="cross-icon"><Icon icon={x} size={15}/></i>
                                </div>
                            )}
                        </div>
                    </div> 
                    <div className="col-md-4">
                        {this.state.inputChips.map((chipsList, i) => {
                            <div className="outline">
                                {this.state.class.map(type =>
                                    <>
                                    <div className={type.Outline} key={i}>
                                        <i className="inputicon"><Icon icon={mapPin} size={16} /></i>
                                        <input name="selection" type="text" value={chipsList.Title} readOnly id={i.id} />
                                        <i className="cross-icon"><Icon icon={x} size={15} /></i>
                                    </div>
                                    <div className={type.Outline} key={i}>
                                        <i className="inputicon"><Icon icon={mapPin} size={16} /></i>
                                        <input name="selection" type="text" value={chipsList.Title} readOnly id={i.id} className="rounded" />
                                        <i className="cross-icon"><Icon icon={x} size={15} /></i>
                                        </div>
                                    </>
                                )}
                            </div>
                        })}
                    </div>
                    {/* <div className="col-md-4">
                        <div className="form-group input-chips filled-chips left-icon"> 
                            <input type="text" readOnly  value="Enabled"/>
                            <i className="inputicon"><Icon icon={mapPin} size={16} /></i>
                            <i className="cross-icon"><Icon icon={x} size={15}/></i>
                        </div>
                        <div className="form-group input-chips filled-chips disabled left-icon"> 
                            <input type="text" readOnly  value="Disabled" disabled/>
                            <i className="inputicon"><Icon icon={mapPin} size={16} /></i>
                            <i className="cross-icon"><Icon icon={x} size={15}/></i>
                        </div>
                        <div className="form-group input-chips filled-chips focus left-icon"> 
                            <input type="text" readOnly  value="Hover" autoFocus/>
                            <i className="inputicon"><Icon icon={mapPin} size={16} /></i>
                            <i className="cross-icon"><Icon icon={x} size={15}/></i>
                        </div>
                        <div className="form-group input-chips filled-chips left-icon"> 
                            <input type="text" readOnly  value="Enabled" className="rounded"/>
                            <i className="inputicon"><Icon icon={mapPin} size={16} /></i>
                            <i className="cross-icon"><Icon icon={x} size={15}/></i>
                        </div>
                        <div className="form-group input-chips filled-chips disabled left-icon"> 
                            <input type="text" readOnly  value="Disabled" disabled className="rounded"/>
                            <i className="inputicon"><Icon icon={mapPin} size={16} /></i>
                            <i className="cross-icon"><Icon icon={x} size={15}/></i>
                        </div>
                        <div className="form-group input-chips filled-chips focus left-icon"> 
                            <input type="text" readOnly  value="Hover" autoFocus className="rounded"/>
                            <i className="inputicon"><Icon icon={mapPin} size={16} /></i>
                            <i className="cross-icon"><Icon icon={x} size={15}/></i>
                        </div>
                    </div>  */}
                </div>
            </div>
        );
    }
}

export default inputChips;
