import React, { Component } from 'react';

class InputSlider extends Component {
  state = {
    value: 0
  }
  handleOnChange = (e) => {
    const { value } = e.target;
    this.setState({ value });
  }
  render() {
    const { id, value, label, disabled, size, min, max, ...inputProps } = this.props;
    return (
      <>
          <div className="range-slider">
            <input type="range" min={min} max={max} value={this.state.value(this.handleOnChange)} onChange={this.handleOnChange}/>
            <span>{this.state.value}</span>
          </div>
      </>
    );
  }
}

export default InputSlider;
